mapper.update:
UPDATE account
SET name=#{name}, username=#{username}, password=#{password}, email=#{email}, phone=#{phone},
status=#{status}, role=#{role}, remark=#{remark}, last_login_at=#{lastLoginAt}, last_login_ip=#{lastLoginIp},
created_at=#{createdAt}, updated_at=#{updatedAt}, deleted_at=#{deletedAt},
created_by=#{createdBy}, updated_by=#{updatedBy}, deleted_by=#{deletedBy}
WHERE id=#{id}
